import {
  Component,
  Input,
  Output,
  EventEmitter,
  ChangeDetectionStrategy,
  InjectionToken,
  Inject,
  Optional,
  ViewEncapsulation,
  ChangeDetectorRef
} from '@angular/core';

export type ESActionHeadingType = 'h1' | 'h2' | 'h3' | 'h4' | 'h5' | 'h6';

export interface ESActonHeadingDefaultOptions {
  type?: ESActionHeadingType;
  typography?: string;
  actionIcon?: string;
  actionSvgIcon?: string;
  actionLabel?: string;
}

export const ES_ACTION_HEADING_DEFAULT_OPTIONS = new InjectionToken<ESActonHeadingDefaultOptions>(
  'ES_ACTION_HEADING_DEFAULT_OPTIONS'
);

@Component({
  selector: 'es-action-heading',
  templateUrl: './action-heading.component.html',
  styleUrls: ['./action-heading.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  encapsulation: ViewEncapsulation.None
})
export class ESActionHeadingComponent {
  private _type: ESActionHeadingType;
  private _typography: string;
  private _actionIcon: string;
  private _actionSvgIcon: string;
  private _actionLabel: string;

  /**
   * Heading text.
   */
  @Input()
  public text: string;

  /**
   * Heading text type.
   */
  @Input()
  public get type(): ESActionHeadingType {
    return this._type;
  }
  public set type(value: ESActionHeadingType) {
    this._type = value || this.defaultOptions?.type || 'h1';
  }

  /**
   * Heading text typography.
   */
  @Input()
  public get typography(): string {
    return this._typography;
  }
  public set typography(value: string) {
    this._typography = value || this.defaultOptions?.typography || 'es-h1';
  }

  /**
   * Action icon.
   */
  @Input()
  public get actionIcon(): string {
    return this._actionIcon;
  }
  public set actionIcon(value: string) {
    this._actionIcon = value || this.defaultOptions?.actionIcon || 'add';
  }

  /**
   * Action svg icon.
   */
  @Input()
  public get actionSvgIcon(): string {
    return this._actionSvgIcon;
  }
  public set actionSvgIcon(value: string) {
    this._actionSvgIcon = value || this.defaultOptions?.actionSvgIcon;
  }

  /**
   * Aria-label of the action button.
   */
  @Input()
  public get actionLabel(): string {
    return this._actionLabel;
  }
  public set actionLabel(value: string) {
    this._actionLabel = value || this.defaultOptions?.actionLabel || 'add';
  }

  /**
   * Color of the action button.
   */
  @Input()
  public color: 'primary' | 'warn' | 'accent' = 'primary';

  /**
   * Event emitted when user clicks button.
   */
  @Output()
  public action: EventEmitter<any> = new EventEmitter();

  /**
   * @ignore
   */
  constructor(
    /**
     * @internal
     */
    public changeDetector: ChangeDetectorRef,
    /**
     * @internal
     */
    @Optional()
    @Inject(ES_ACTION_HEADING_DEFAULT_OPTIONS)
    private defaultOptions: ESActonHeadingDefaultOptions
  ) {
    this.type = this.defaultOptions?.type;
    this.typography = this.defaultOptions?.typography;
  }
}

import {
  Component,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  ViewEncapsulation,
  Input,
  Output,
  EventEmitter,
  InjectionToken,
  Optional,
  Inject
} from '@angular/core';

import { coerceBooleanProperty } from '@angular/cdk/coercion';

import { Observable } from 'rxjs';

import { ESAlertVariant } from './alert.types';
import { ESLocaleService, ESLocale } from '../locale';

export interface ESAlertDefaultOptions {
  typography?: string;
  titleTypography?: string;
  iconMapping?: { [key in ESAlertVariant]?: { icon?: string; svgIcon?: string } };
}

const DEFAULT_TYPOGRAPHY = 'es-body-100';
const DEFAULT_TITLE_TYPOGRAPHY = 'es-body-100';

const DEFAULT_ICON_MAPPING = {
  default: { icon: 'info' },
  info: { icon: 'info' },
  success: { icon: 'check_circle' },
  warning: { icon: 'warning' },
  error: { icon: 'error' }
};

export const ES_ALERT_DEFAULT_OPTIONS = new InjectionToken<ESAlertDefaultOptions>(
  'ES_ALERT_DEFAULT_OPTIONS'
);

@Component({
  selector: 'es-alert',
  templateUrl: './alert.component.html',
  styleUrls: ['./alert.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  encapsulation: ViewEncapsulation.None
})
export class ESAlertComponent {
  private iconMapping: { [key in ESAlertVariant]: { icon?: string; svgIcon?: string } };

  /**
   * The variant of the alert. This defines the color and icon used.
   */
  @Input() public variant: ESAlertVariant = 'default';

  private _typography;

  /**
   * Class applied to text.
   */
  @Input()
  public get typography(): string {
    return this._typography;
  }
  public set typography(value: string) {
    this._typography =
      value || (this.defaultOptions && this.defaultOptions.typography) || DEFAULT_TYPOGRAPHY;
  }

  private _titleTypography;

  /**
   * Class applied to title.
   */
  @Input()
  public get titleTypography(): string {
    return this._titleTypography;
  }
  public set titleTypography(value: string) {
    this._titleTypography =
      value ||
      (this.defaultOptions && this.defaultOptions.titleTypography) ||
      DEFAULT_TITLE_TYPOGRAPHY;
  }

  private _closable = false;

  /**
   * Show close button.
   */
  @Input()
  public get closable() {
    return this._closable;
  }
  public set closable(closable: any) {
    this._closable = coerceBooleanProperty(closable);
  }

  /**
   * Override the icon displayed before the text.
   * Unless provided, the icon is mapped to the value of the variant input.
   */
  @Input() public icon?: string;

  /**
   * Override the icon displayed before the text.
   * Unless provided, the icon is mapped to the value of the variant input.
   */
  @Input() public svgIcon?: string;

  /**
   * Event emitted when user clicks close button.
   */
  @Output() public closed = new EventEmitter();

  /**
   * @internal
   * @ignore
   */
  public locale$: Observable<ESLocale>;

  /**
   * @ignore
   */
  constructor(
    /**
     * @internal
     */
    public changeDetector: ChangeDetectorRef,
    /**
     * @internal
     */
    private localeService: ESLocaleService,
    /**
     * @internal
     */
    @Optional()
    @Inject(ES_ALERT_DEFAULT_OPTIONS)
    private defaultOptions: ESAlertDefaultOptions
  ) {
    this.locale$ = this.localeService.locale();

    this.typography = (defaultOptions && defaultOptions.typography) || DEFAULT_TYPOGRAPHY;
    this.titleTypography =
      (defaultOptions && defaultOptions.titleTypography) || DEFAULT_TITLE_TYPOGRAPHY;
    this.iconMapping = { ...DEFAULT_ICON_MAPPING, ...defaultOptions?.iconMapping };
  }

  /**
   * @internal
   * @ignore
   */
  public onClose() {
    this.closed.emit();
  }

  public get currentIcon() {
    if (this.icon) {
      return { icon: this.icon };
    }
    if (this.svgIcon) {
      return { svgIcon: this.svgIcon };
    }
    return this.iconMapping[this.variant];
  }
}

import {
  Component,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  ViewEncapsulation,
  OnInit,
  OnDestroy,
  Input,
  ViewChild,
  HostBinding,
  Optional,
  Self,
  Host,
  ElementRef,
  AfterViewInit,
  Renderer2,
  Inject,
  InjectionToken
} from '@angular/core';
import {
  ControlValueAccessor,
  UntypedFormControl,
  UntypedFormGroup,
  FormGroupDirective,
  NgControl
} from '@angular/forms';

import { FocusMonitor } from '@angular/cdk/a11y';
import { coerceBooleanProperty } from '@angular/cdk/coercion';
import { CdkOverlayOrigin } from '@angular/cdk/overlay';

import { MatButton } from '@angular/material/button';
import { MatChipListbox } from '@angular/material/chips';
import { MatFormField, MatFormFieldControl } from '@angular/material/form-field';
import { MatSelectionList, MatSelectionListChange } from '@angular/material/list';

import { Observable, Subject, combineLatest, of, BehaviorSubject } from 'rxjs';
import {
  catchError,
  debounceTime,
  filter,
  map,
  shareReplay,
  startWith,
  switchMap,
  takeUntil,
  tap
} from 'rxjs/operators';

import { resizeObserver } from '../../utils/resize-observer';
import { ESLocale, ESLocaleService } from '../locale';

import { ES_AUTOCOMPLETE_ANIMATIONS } from './autocomplete-multiple.animations';
import { ESAutocompleteMultipleSearchScope } from '.';

export interface ESAutocompleteMultipleDefaultOptionsSizes {
  chipLeftMargin: number;
  countBadgeMaxWidth: number;
}

export interface ESAutocompleteMultipleDefaultOptions {
  sizes?: ESAutocompleteMultipleDefaultOptionsSizes;
}

export const ES_AUTOCOMPLETE_MULTIPLE_DEFAULT_SIZES = {
  chipLeftMargin: 4,
  countBadgeMaxWidth: 40
};

export const ES_AUTOCOMPLETE_MULTIPLE_DEFAULT_OPTIONS = new InjectionToken<ESAutocompleteMultipleDefaultOptions>(
  'ES_AUTOCOMPLETE_MULTIPLE_DEFAULT_OPTIONS'
);

const MIN_PANEL_WIDTH = 320;

@Component({
  selector: 'es-autocomplete-multiple',
  templateUrl: './autocomplete-multiple.component.html',
  styleUrls: ['./autocomplete-multiple.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  encapsulation: ViewEncapsulation.None,
  providers: [{ provide: MatFormFieldControl, useExisting: ESAutocompleteMultipleComponent }],
  animations: ES_AUTOCOMPLETE_ANIMATIONS
})
export class ESAutocompleteMultipleComponent
  implements MatFormFieldControl<any[]>, ControlValueAccessor, OnInit, OnDestroy, AfterViewInit {
  private static nextId = 0;
  @HostBinding() public id = `es-autocomplete-multiple-${ESAutocompleteMultipleComponent.nextId++}`;
  @HostBinding('style.display') public styleDisplay = 'block';
  @HostBinding('style.width') public styleWidth = '100%';

  @ViewChild('input', { static: false }) private input?: ElementRef<HTMLInputElement>;
  @ViewChild('arrow', { static: true }) private arrow?: MatButton;
  @ViewChild('selectionList', { static: false }) private selectionList?: MatSelectionList;
  @ViewChild('chipList', { static: false }) private chipList?: MatChipListbox;

  /** Options search service. When second argument is present, the search should be performed by passed down options. */
  @Input() public service!: (search: string, options?: any[]) => Observable<any[]>;

  /** Function that maps an option control value to its display value in the trigger. */
  @Input() public displayWith!: (option: any) => string;

  /** Delay before starting search after entering search text. */
  @Input() public searchDebounce = 400;

  /** Placeholder for search input. */
  @Input()
  public get placeholder() {
    return this._placeholder;
  }
  public set placeholder(placeholder) {
    this._placeholder = placeholder;
    this.stateChanges.next();
  }
  private _placeholder = '';

  /** Whether the control is required. */
  @Input()
  public get required() {
    return this._required;
  }
  public set required(required) {
    this._required = coerceBooleanProperty(required);
    this.stateChanges.next();
  }
  private _required = false;

  /** Whether the control is disabled. */
  @Input()
  public get disabled() {
    return this._disabled;
  }
  public set disabled(disabled) {
    this._disabled = coerceBooleanProperty(disabled);
    this.stateChanges.next();
  }
  private _disabled = false;

  /**
   * Sizes of component elements which are used for collapse calculations.
   */
  @Input()
  public get sizes(): ESAutocompleteMultipleDefaultOptionsSizes {
    return this._sizes;
  }
  public set sizes(value: ESAutocompleteMultipleDefaultOptionsSizes) {
    this._sizes = value || this.defaultOptions?.sizes || ES_AUTOCOMPLETE_MULTIPLE_DEFAULT_SIZES;
  }
  private _sizes;

  /** Total number of options. */
  @Input()
  public get optionsCount(): number {
    return this._optionsCount || 0;
  }
  public set optionsCount(value: number) {
    this._optionsCount = value || 0;
  }
  private _optionsCount;

  /**
   * @internal
   * @ignore
   */
  public locale$: Observable<ESLocale>;

  /**
   * @internal
   * @ignore
   */
  public origin!: CdkOverlayOrigin;

  /**
   * @ignore
   */
  public describedBy = '';

  /**
   * @internal
   * @ignore
   */
  private destoryed$ = new Subject<void>();

  /**
   * @ignore
   */
  public stateChanges = new Subject<void>();

  /**
   * @ignore
   */
  public value: any[] = [];

  /**
   * @internal
   * @ignore
   */
  public isOpen = false;

  /**
   * @internal
   * @ignore
   */
  public width = 0;

  /**
   * @internal
   * @ignore
   */
  public selectionChanged$ = new BehaviorSubject(null);

  /**
   * @internal
   * @ignore
   */
  public options$: Observable<any[]>;

  /**
   * @internal
   * @ignore
   */
  public filteredOptions$: Observable<any[]>;

  /**
   * @ignore
   */
  public focused = false;

  /**
   * @internal
   * @ignore
   */
  public searchScope = ESAutocompleteMultipleSearchScope;

  /**
   * @internal
   * @ignore
   */
  public form = new UntypedFormGroup({
    scope: new UntypedFormControl(this.searchScope.ALL),
    text: new UntypedFormControl('')
  });

  /**
   * @internal
   * @ignore
   */
  public hiddenChipCount = 0;

  /**
   * @internal
   * @ignore
   */
  private filteredOptionsCount = 0;

  /**
   * @ignore
   */
  @HostBinding('class.floating')
  public get shouldLabelFloat() {
    return this.focused || !this.empty;
  }

  /**
   * @ignore
   */
  public get errorState(): boolean {
    const control = this.ngControl;
    const form = this.ngForm;

    if (control) {
      return !!(control.invalid && (control.touched || form?.submitted));
    }

    return false;
  }

  /**
   * @ignore
   */
  public get empty() {
    return !this.value.length;
  }

  /**
   * @ignore
   */
  constructor(
    @Optional() @Self() public ngControl: NgControl,
    @Optional() public ngForm: FormGroupDirective,
    /**
     * @internal
     */
    @Optional() @Host() private matFormField: MatFormField,
    @Optional()
    @Inject(ES_AUTOCOMPLETE_MULTIPLE_DEFAULT_OPTIONS)
    private defaultOptions: ESAutocompleteMultipleDefaultOptions,
    /**
     * @internal
     */
    private changeDetectorRef: ChangeDetectorRef,
    /**
     * @internal
     */
    private focusMonitor: FocusMonitor,
    /**
     * @internal
     */
    private rendered2: Renderer2,
    /**
     * @internal
     */
    private elementRef: ElementRef,
    /**
     * @internal
     */
    public localeService: ESLocaleService
  ) {
    if (this.ngControl != null) {
      this.ngControl.valueAccessor = this as any;
    }
    this.locale$ = this.localeService.locale();

    this.sizes = defaultOptions?.sizes || ES_AUTOCOMPLETE_MULTIPLE_DEFAULT_SIZES;

    this.options$ = this.form.valueChanges.pipe(
      startWith({ scope: this.searchScope.ALL, text: '' }),
      debounceTime(this.searchDebounce),
      switchMap(({ scope, text }) => {
        this.filteredOptionsCount = 0;

        if (scope === this.searchScope.SELECTED) {
          return this.service(text, this.value).pipe(catchError(() => of([])));
        }

        return this.service(text).pipe(catchError(() => of([])));
      }),
      shareReplay(1)
    );

    this.filteredOptions$ = combineLatest([
      this.options$,
      this.selectionChanged$.pipe(filter(() => this.isOpen))
    ]).pipe(
      debounceTime(100),
      map(([options]) => {
        const { scope } = this.form.value;

        if (scope === this.searchScope.ALL) {
          return options;
        }

        const filtered =
          scope === this.searchScope.SELECTED
            ? options.filter((option) => this.value.some((o) => o.id === option.id))
            : options.filter((option) => !this.value.some((o) => o.id === option.id));

        this.optionsCount = this.optionsCount - options.length + filtered.length;

        return filtered;
      }),
      tap((options) => {
        this.filteredOptionsCount = options.length;
      })
    );
  }

  /**
   * @ignore
   */
  public ngOnInit() {
    if (this.matFormField) {
      this.origin = {
        elementRef: this.matFormField.getConnectedOverlayOrigin()
      };
    }

    this.stateChanges.subscribe(() => {
      this.changeDetectorRef.detectChanges();
    });
  }

  /**
   * @ignore
   */
  public ngAfterViewInit() {
    combineLatest([
      // tslint:disable-next-line:deprecation
      resizeObserver(this.elementRef.nativeElement).pipe(startWith(null), debounceTime(10)),
      this.selectionChanged$
    ])
      .pipe(takeUntil(this.destoryed$))
      .subscribe(() => {
        this.updateDisplayedChips();
      });
  }

  /**
   * @ignore
   */
  public ngOnDestroy() {
    this.destoryed$.next();
    this.destoryed$.complete();
    this.stateChanges.complete();
  }

  /**
   * @ignore
   */
  public setDescribedByIds(ids: string[]) {
    this.describedBy = ids.join(' ');
  }

  /**
   * @ignore
   */
  public onContainerClick() {
    if (this.ngControl.disabled || this.disabled) {
      return;
    }

    this.isOpen = true;
    if (this.matFormField) {
      const width = this.matFormField._elementRef.nativeElement.clientWidth;

      this.width = width < MIN_PANEL_WIDTH ? MIN_PANEL_WIDTH : width;
    }
    this.stateChanges.next();

    setTimeout(() => {
      if (this.input) {
        this.input.nativeElement.focus();
      }
    });
  }

  /**
   * @ignore
   */
  public writeValue(value: any[]) {
    if (value) {
      this.value = value;
      this.stateChanges.next();
    }
  }

  /**
   * @ignore
   */
  public registerOnChange(onChange: (value: any) => void) {
    this.onChange = onChange;
  }

  /**
   * @ignore
   */
  public onChange = (_: any[]) => {};

  /**
   * @ignore
   */
  public registerOnTouched(onTouched: () => void) {
    this.onTouched = onTouched;
  }

  /**
   * @ignore
   */
  public onTouched = () => {};

  /**
   * @internal
   * @ignore
   */
  public onClose(shouldFocusArrow?: boolean) {
    this.onTouched();
    this.isOpen = false;
    this.form.patchValue({ text: '', scope: this.searchScope.ALL });

    if (shouldFocusArrow && this.arrow) {
      this.focusMonitor.focusVia(this.arrow._elementRef.nativeElement, 'keyboard');
    }

    this.stateChanges.next();
  }

  /**
   * @internal
   * @ignore
   */
  public onClear() {
    this.form.patchValue({ text: '' });
  }

  /**
   * @internal
   * @ignore
   */
  public onSelectionChange(event: MatSelectionListChange) {
    const newValue = this.value.slice();
    const option = event.options[0].value;

    const index = newValue.findIndex((o) => o.id === option.id);
    if (index !== -1) {
      newValue.splice(index, 1);
    } else {
      newValue.push(option);
    }

    this.value = newValue;
    this.changeState(this.value);
  }

  /**
   * @internal
   * @ignore
   */
  public onSelectAll() {
    const newValue = this.value.slice();

    this.selectionList.options.forEach((option) => {
      const index = newValue.findIndex((o) => o.id === option.value.id);

      if (index === -1) {
        newValue.push(option.value);
      }
    });

    this.value = newValue;
    this.changeState(this.value);
  }

  /**
   * @internal
   * @ignore
   */
  public onDeSelectAll() {
    if (this.value.length) {
      const newValue = this.value.slice();

      this.selectionList.options.forEach((option) => {
        const index = newValue.findIndex((o) => o.id === option.value.id);

        if (index !== -1) {
          newValue.splice(index, 1);
        }
      });

      this.value = newValue;
      this.changeState(this.value);
    }
  }

  /**
   * @internal
   * @ignore
   */
  public onRemoveAll(event?: Event) {
    event.stopPropagation();

    this.value = [];
    this.changeState(this.value);
  }

  /**
   * @internal
   * @ignore
   */
  public onRemove(index: number) {
    this.value.splice(index, 1);
    this.changeState(this.value);
  }

  /**
   * @internal
   * @ignore
   */
  public onShowSelectedTab() {
    this.form.patchValue({ scope: this.searchScope.SELECTED });
  }

  /**
   * @internal
   * @ignore
   */
  public isSelected(option: any) {
    return !!this.value.find((o) => o.id === option.id);
  }

  /**
   * @internal
   * @ignore
   */
  public getShownCountInfo(labelShown: string, labelOf: string) {
    return `${labelShown}: ${this.filteredOptionsCount} ${labelOf} ${this.optionsCount}`;
  }

  /**
   * @internal
   * @ignore
   */
  public updateDisplayedChips() {
    if (this.chipList) {
      let count = 0;

      const chips = this.chipList._chips;

      let isOverflow = false;
      let offset = 0;

      chips.forEach((chip, index) => {
        this.rendered2.setStyle(chip._elementRef.nativeElement, 'display', 'inline-flex');
        this.rendered2.setStyle(chip._elementRef.nativeElement, 'width', 'auto');

        const {
          width: chipListWidth
        } = chip._elementRef.nativeElement.parentElement.getBoundingClientRect();

        const { width: chipWidth } = chip._elementRef.nativeElement.getBoundingClientRect();

        offset += chipWidth;

        if (isOverflow) {
          count += 1;

          this.rendered2.setStyle(chip._elementRef.nativeElement, 'display', 'none');
        } else if (chips.length === 1 && chipWidth > chipListWidth) {
          this.rendered2.setStyle(chip._elementRef.nativeElement, 'width', `${chipListWidth}px`);
        } else if (chips.length === 1) {
          this.rendered2.setStyle(chip._elementRef.nativeElement, 'width', 'auto');
        } else {
          if (offset <= chipListWidth && index === chips.length - 1) {
            return;
          } else if (offset > chipListWidth - this.sizes.countBadgeMaxWidth && index) {
            count += 1;

            isOverflow = true;

            this.rendered2.setStyle(chip._elementRef.nativeElement, 'display', 'none');
          } else {
            const width =
              chipWidth + this.sizes.countBadgeMaxWidth > chipListWidth
                ? chipListWidth - this.sizes.countBadgeMaxWidth
                : chipWidth;

            this.rendered2.setStyle(chip._elementRef.nativeElement, 'width', `${width}px`);
          }
        }

        offset += this.sizes.chipLeftMargin;
      });

      if (this.hiddenChipCount !== count) {
        this.hiddenChipCount = count;
        this.stateChanges.next();
      }
    }
  }

  /**
   * @internal
   * @ignore
   */
  private changeState(value: any[]) {
    this.onChange(value);
    this.stateChanges.next();
    this.selectionChanged$.next(true);
  }
}

import {
  Component,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  ViewEncapsulation,
  OnInit,
  OnDestroy,
  Input,
  ViewChild,
  HostBinding,
  Optional,
  Self,
  Host,
  ElementRef,
  AfterViewInit,
  Renderer2,
  Inject,
  InjectionToken
} from '@angular/core';
import {
  ControlValueAccessor,
  UntypedFormControl,
  UntypedFormGroup,
  FormGroupDirective,
  NgControl
} from '@angular/forms';

import { FocusMonitor } from '@angular/cdk/a11y';
import { coerceBooleanProperty } from '@angular/cdk/coercion';
import { CdkOverlayOrigin } from '@angular/cdk/overlay';

import { MatButton } from '@angular/material/button';
import { MatChipListbox } from '@angular/material/chips';
import { MatFormField, MatFormFieldControl } from '@angular/material/form-field';
import { MatSelectionList, MatSelectionListChange } from '@angular/material/list';

import { Observable, Subject, combineLatest, of, BehaviorSubject } from 'rxjs';
import {
  catchError,
  debounceTime,
  filter,
  map,
  shareReplay,
  startWith,
  switchMap,
  takeUntil,
  tap
} from 'rxjs/operators';

import { resizeObserver } from '../../utils/resize-observer';
import { ESLocale, ESLocaleService } from '../locale';

import { ES_AUTOCOMPLETE_ANIMATIONS } from './autocomplete-multiple.animations';
import { ESAutocompleteMultipleSearchScope } from '.';

export interface ESAutocompleteMultipleDefaultOptionsSizes {
  chipLeftMargin: number;
  countBadgeMaxWidth: number;
}

export interface ESAutocompleteMultipleDefaultOptions {
  sizes?: ESAutocompleteMultipleDefaultOptionsSizes;
}

export const ES_AUTOCOMPLETE_MULTIPLE_DEFAULT_SIZES = {
  chipLeftMargin: 4,
  countBadgeMaxWidth: 40
};

export const ES_AUTOCOMPLETE_MULTIPLE_DEFAULT_OPTIONS = new InjectionToken<ESAutocompleteMultipleDefaultOptions>(
  'ES_AUTOCOMPLETE_MULTIPLE_DEFAULT_OPTIONS'
);

const MIN_PANEL_WIDTH = 320;

@Component({
  selector: 'es-autocomplete-multiple',
  templateUrl: './autocomplete-multiple.component.html',
  styleUrls: ['./autocomplete-multiple.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  encapsulation: ViewEncapsulation.None,
  providers: [{ provide: MatFormFieldControl, useExisting: ESAutocompleteMultipleComponent }],
  animations: ES_AUTOCOMPLETE_ANIMATIONS
})
export class ESAutocompleteMultipleComponent
  implements MatFormFieldControl<any[]>, ControlValueAccessor, OnInit, OnDestroy, AfterViewInit {
  private static nextId = 0;
  @HostBinding() public id = `es-autocomplete-multiple-${ESAutocompleteMultipleComponent.nextId++}`;
  @HostBinding('style.display') public styleDisplay = 'block';
  @HostBinding('style.width') public styleWidth = '100%';

  @ViewChild('input', { static: false }) private input?: ElementRef<HTMLInputElement>;
  @ViewChild('arrow', { static: true }) private arrow?: MatButton;
  @ViewChild('selectionList', { static: false }) private selectionList?: MatSelectionList;
  @ViewChild('chipList', { static: false }) private chipList?: MatChipListbox;

  /** Options search service. When second argument is present, the search should be performed by passed down options. */
  @Input() public service!: (search: string, options?: any[]) => Observable<any[]>;

  /** Function that maps an option control value to its display value in the trigger. */
  @Input() public displayWith!: (option: any) => string;

  /** Delay before starting search after entering search text. */
  @Input() public searchDebounce = 400;

  /** Placeholder for search input. */
  @Input()
  public get placeholder() {
    return this._placeholder;
  }
  public set placeholder(placeholder) {
    this._placeholder = placeholder;
    this.stateChanges.next();
  }
  private _placeholder = '';

  /** Whether the control is required. */
  @Input()
  public get required() {
    return this._required;
  }
  public set required(required) {
    this._required = coerceBooleanProperty(required);
    this.stateChanges.next();
  }
  private _required = false;

  /** Whether the control is disabled. */
  @Input()
  public get disabled() {
    return this._disabled;
  }
  public set disabled(disabled) {
    this._disabled = coerceBooleanProperty(disabled);
    this.stateChanges.next();
  }
  private _disabled = false;

  /**
   * Sizes of component elements which are used for collapse calculations.
   */
  @Input()
  public get sizes(): ESAutocompleteMultipleDefaultOptionsSizes {
    return this._sizes;
  }
  public set sizes(value: ESAutocompleteMultipleDefaultOptionsSizes) {
    this._sizes = value || this.defaultOptions?.sizes || ES_AUTOCOMPLETE_MULTIPLE_DEFAULT_SIZES;
  }
  private _sizes;

  /** Total number of options. */
  @Input()
  public get optionsCount(): number {
    return this._optionsCount || 0;
  }
  public set optionsCount(value: number) {
    this._optionsCount = value || 0;
  }
  private _optionsCount;

  /**
   * @internal
   * @ignore
   */
  public locale$: Observable<ESLocale>;

  /**
   * @internal
   * @ignore
   */
  public origin!: CdkOverlayOrigin;

  /**
   * @ignore
   */
  public describedBy = '';

  /**
   * @internal
   * @ignore
   */
  private destoryed$ = new Subject<void>();

  /**
   * @ignore
   */
  public stateChanges = new Subject<void>();

  /**
   * @ignore
   */
  public value: any[] = [];

  /**
   * @internal
   * @ignore
   */
  public isOpen = false;

  /**
   * @internal
   * @ignore
   */
  public width = 0;

  /**
   * @internal
   * @ignore
   */
  public selectionChanged$ = new BehaviorSubject(null);

  /**
   * @internal
   * @ignore
   */
  public options$: Observable<any[]>;

  /**
   * @internal
   * @ignore
   */
  public filteredOptions$: Observable<any[]>;

  /**
   * @ignore
   */
  public focused = false;

  /**
   * @internal
   * @ignore
   */
  public searchScope = ESAutocompleteMultipleSearchScope;

  /**
   * @internal
   * @ignore
   */
  public form = new UntypedFormGroup({
    scope: new UntypedFormControl(this.searchScope.ALL),
    text: new UntypedFormControl('')
  });

  /**
   * @internal
   * @ignore
   */
  public hiddenChipCount = 0;

  /**
   * @internal
   * @ignore
   */
  private filteredOptionsCount = 0;

  /**
   * @ignore
   */
  @HostBinding('class.floating')
  public get shouldLabelFloat() {
    return this.focused || !this.empty;
  }

  /**
   * @ignore
   */
  public get errorState(): boolean {
    const control = this.ngControl;
    const form = this.ngForm;

    if (control) {
      return !!(control.invalid && (control.touched || form?.submitted));
    }

    return false;
  }

  /**
   * @ignore
   */
  public get empty() {
    return !this.value.length;
  }

  /**
   * @ignore
   */
  constructor(
    @Optional() @Self() public ngControl: NgControl,
    @Optional() public ngForm: FormGroupDirective,
    /**
     * @internal
     */
    @Optional() @Host() private matFormField: MatFormField,
    @Optional()
    @Inject(ES_AUTOCOMPLETE_MULTIPLE_DEFAULT_OPTIONS)
    private defaultOptions: ESAutocompleteMultipleDefaultOptions,
    /**
     * @internal
     */
    private changeDetectorRef: ChangeDetectorRef,
    /**
     * @internal
     */
    private focusMonitor: FocusMonitor,
    /**
     * @internal
     */
    private rendered2: Renderer2,
    /**
     * @internal
     */
    private elementRef: ElementRef,
    /**
     * @internal
     */
    public localeService: ESLocaleService
  ) {
    if (this.ngControl != null) {
      this.ngControl.valueAccessor = this as any;
    }
    this.locale$ = this.localeService.locale();

    this.sizes = defaultOptions?.sizes || ES_AUTOCOMPLETE_MULTIPLE_DEFAULT_SIZES;

    this.options$ = this.form.valueChanges.pipe(
      startWith({ scope: this.searchScope.ALL, text: '' }),
      debounceTime(this.searchDebounce),
      switchMap(({ scope, text }) => {
        this.filteredOptionsCount = 0;

        if (scope === this.searchScope.SELECTED) {
          return this.service(text, this.value).pipe(catchError(() => of([])));
        }

        return this.service(text).pipe(catchError(() => of([])));
      }),
      shareReplay(1)
    );

    this.filteredOptions$ = combineLatest([
      this.options$,
      this.selectionChanged$.pipe(filter(() => this.isOpen))
    ]).pipe(
      debounceTime(100),
      map(([options]) => {
        const { scope } = this.form.value;

        if (scope === this.searchScope.ALL) {
          return options;
        }

        const filtered =
          scope === this.searchScope.SELECTED
            ? options.filter((option) => this.value.some((o) => o.id === option.id))
            : options.filter((option) => !this.value.some((o) => o.id === option.id));

        this.optionsCount = this.optionsCount - options.length + filtered.length;

        return filtered;
      }),
      tap((options) => {
        this.filteredOptionsCount = options.length;
      })
    );
  }

  /**
   * @ignore
   */
  public ngOnInit() {
    if (this.matFormField) {
      this.origin = {
        elementRef: this.matFormField.getConnectedOverlayOrigin()
      };
    }

    this.stateChanges.subscribe(() => {
      this.changeDetectorRef.detectChanges();
    });
  }

  /**
   * @ignore
   */
  public ngAfterViewInit() {
    combineLatest([
      // tslint:disable-next-line:deprecation
      resizeObserver(this.elementRef.nativeElement).pipe(startWith(null), debounceTime(10)),
      this.selectionChanged$
    ])
      .pipe(takeUntil(this.destoryed$))
      .subscribe(() => {
        this.updateDisplayedChips();
      });
  }

  /**
   * @ignore
   */
  public ngOnDestroy() {
    this.destoryed$.next();
    this.destoryed$.complete();
    this.stateChanges.complete();
  }

  /**
   * @ignore
   */
  public setDescribedByIds(ids: string[]) {
    this.describedBy = ids.join(' ');
  }

  /**
   * @ignore
   */
  public onContainerClick() {
    if (this.ngControl.disabled || this.disabled) {
      return;
    }

    this.isOpen = true;
    if (this.matFormField) {
      const width = this.matFormField._elementRef.nativeElement.clientWidth;

      this.width = width < MIN_PANEL_WIDTH ? MIN_PANEL_WIDTH : width;
    }
    this.stateChanges.next();

    setTimeout(() => {
      if (this.input) {
        this.input.nativeElement.focus();
      }
    });
  }

  /**
   * @ignore
   */
  public writeValue(value: any[]) {
    if (value) {
      this.value = value;
      this.stateChanges.next();
    }
  }

  /**
   * @ignore
   */
  public registerOnChange(onChange: (value: any) => void) {
    this.onChange = onChange;
  }

  /**
   * @ignore
   */
  public onChange = (_: any[]) => {};

  /**
   * @ignore
   */
  public registerOnTouched(onTouched: () => void) {
    this.onTouched = onTouched;
  }

  /**
   * @ignore
   */
  public onTouched = () => {};

  /**
   * @internal
   * @ignore
   */
  public onClose(shouldFocusArrow?: boolean) {
    this.onTouched();
    this.isOpen = false;
    this.form.patchValue({ text: '', scope: this.searchScope.ALL });

    if (shouldFocusArrow && this.arrow) {
      this.focusMonitor.focusVia(this.arrow._elementRef.nativeElement, 'keyboard');
    }

    this.stateChanges.next();
  }

  /**
   * @internal
   * @ignore
   */
  public onClear() {
    this.form.patchValue({ text: '' });
  }

  /**
   * @internal
   * @ignore
   */
  public onSelectionChange(event: MatSelectionListChange) {
    const newValue = this.value.slice();
    const option = event.options[0].value;

    const index = newValue.findIndex((o) => o.id === option.id);
    if (index !== -1) {
      newValue.splice(index, 1);
    } else {
      newValue.push(option);
    }

    this.value = newValue;
    this.changeState(this.value);
  }

  /**
   * @internal
   * @ignore
   */
  public onSelectAll() {
    const newValue = this.value.slice();

    this.selectionList.options.forEach((option) => {
      const index = newValue.findIndex((o) => o.id === option.value.id);

      if (index === -1) {
        newValue.push(option.value);
      }
    });

    this.value = newValue;
    this.changeState(this.value);
  }

  /**
   * @internal
   * @ignore
   */
  public onDeSelectAll() {
    if (this.value.length) {
      const newValue = this.value.slice();

      this.selectionList.options.forEach((option) => {
        const index = newValue.findIndex((o) => o.id === option.value.id);

        if (index !== -1) {
          newValue.splice(index, 1);
        }
      });

      this.value = newValue;
      this.changeState(this.value);
    }
  }

  /**
   * @internal
   * @ignore
   */
  public onRemoveAll(event?: Event) {
    event.stopPropagation();

    this.value = [];
    this.changeState(this.value);
  }

  /**
   * @internal
   * @ignore
   */
  public onRemove(index: number) {
    this.value.splice(index, 1);
    this.changeState(this.value);
  }

  /**
   * @internal
   * @ignore
   */
  public onShowSelectedTab() {
    this.form.patchValue({ scope: this.searchScope.SELECTED });
  }

  /**
   * @internal
   * @ignore
   */
  public isSelected(option: any) {
    return !!this.value.find((o) => o.id === option.id);
  }

  /**
   * @internal
   * @ignore
   */
  public getShownCountInfo(labelShown: string, labelOf: string) {
    return `${labelShown}: ${this.filteredOptionsCount} ${labelOf} ${this.optionsCount}`;
  }

  /**
   * @internal
   * @ignore
   */
  public updateDisplayedChips() {
    if (this.chipList) {
      let count = 0;

      const chips = this.chipList._chips;

      let isOverflow = false;
      let offset = 0;

      chips.forEach((chip, index) => {
        this.rendered2.setStyle(chip._elementRef.nativeElement, 'display', 'inline-flex');
        this.rendered2.setStyle(chip._elementRef.nativeElement, 'width', 'auto');

        const {
          width: chipListWidth
        } = chip._elementRef.nativeElement.parentElement.getBoundingClientRect();

        const { width: chipWidth } = chip._elementRef.nativeElement.getBoundingClientRect();

        offset += chipWidth;

        if (isOverflow) {
          count += 1;

          this.rendered2.setStyle(chip._elementRef.nativeElement, 'display', 'none');
        } else if (chips.length === 1 && chipWidth > chipListWidth) {
          this.rendered2.setStyle(chip._elementRef.nativeElement, 'width', `${chipListWidth}px`);
        } else if (chips.length === 1) {
          this.rendered2.setStyle(chip._elementRef.nativeElement, 'width', 'auto');
        } else {
          if (offset <= chipListWidth && index === chips.length - 1) {
            return;
          } else if (offset > chipListWidth - this.sizes.countBadgeMaxWidth && index) {
            count += 1;

            isOverflow = true;

            this.rendered2.setStyle(chip._elementRef.nativeElement, 'display', 'none');
          } else {
            const width =
              chipWidth + this.sizes.countBadgeMaxWidth > chipListWidth
                ? chipListWidth - this.sizes.countBadgeMaxWidth
                : chipWidth;

            this.rendered2.setStyle(chip._elementRef.nativeElement, 'width', `${width}px`);
          }
        }

        offset += this.sizes.chipLeftMargin;
      });

      if (this.hiddenChipCount !== count) {
        this.hiddenChipCount = count;
        this.stateChanges.next();
      }
    }
  }

  /**
   * @internal
   * @ignore
   */
  private changeState(value: any[]) {
    this.onChange(value);
    this.stateChanges.next();
    this.selectionChanged$.next(true);
  }
}

export interface ESAvatarDefaultOptions {
  size?: number;
  typography?: string;
  variant?: ESAvatarVariant;
