import { Injectable } from '@angular/core';

import { Observable, of } from 'rxjs';
import { debounceTime, map } from 'rxjs/operators';

const OPTIONS = [
  { id: 1, name: 'Estonia' },
  { id: 2, name: 'Iceland' },
  { id: 3, name: 'Norway' },
  { id: 4, name: 'Lithuania' },
  { id: 5, name: 'Sweden' },
  { id: 6, name: 'Austria' },
  { id: 7, name: 'Switzerland' },
  { id: 8, name: 'Albania' },
  { id: 9, name: 'Portugal' },
  { id: 10, name: 'Russia' },
  { id: 11, name: 'India' },
  { id: 12, name: 'Oman' }
];

@Injectable()
export class AutocompleteMultipleStoryService {
  public getOptions(text: string, count?: number): Observable<any> {
    return of(OPTIONS).pipe(
      debounceTime(1000),
      map((options) => options.filter((option) => option.name.toLowerCase().includes(text))),
      map((options) => {
        const o = count ? options.slice(0, count) : options;

        return {
          options: o,
          totalCount: count ? Math.min(count, o.length) : o.length
        };
      })
    );
  }
}

import { Injectable } from '@angular/core';
import { ActivatedRouteSnapshot } from '@angular/router';

import { CategoriesService, ItemsService } from './breadcrumbs.spec.service';

@Injectable()
export class CategoriesListResolver {
  constructor(private categoriesService: CategoriesService) {}

  public resolve() {
    return this.categoriesService.getAll();
  }
}

@Injectable()
export class CategoriesShowResolver {
  constructor(private categoriesService: CategoriesService) {}

  public resolve(route: ActivatedRouteSnapshot) {
    return this.categoriesService.getOne(+route.params.item);
  }
}

