import { Component, ChangeDetectionStrategy, Input, Output, EventEmitter } from '@angular/core';

import { DomSanitizer } from '@angular/platform-browser';
import { MatIconRegistry } from '@angular/material/icon';

@Component({
  selector: 'es-alert-story-icon-mapping',
  template: `
    <es-alert variant="default" icon="new_releases" [closable]="closable" (closed)="onClose()">
      <ng-container role="title">{{ title }}</ng-container>
      {{ content }}
    </es-alert>
    <br />
    <es-alert variant="info" svgIcon="warning" [closable]="closable" (closed)="onClose()">
      <ng-container role="title">{{ title }}</ng-container>
      {{ content }}
    </es-alert>
    <br />
    <es-alert variant="success" [closable]="closable" (closed)="onClose()">
      <ng-container role="title">{{ title }}</ng-container>
      {{ content }}</es-alert
    >
    <br />
    <es-alert variant="warning" [closable]="closable" (closed)="onClose()">
      <ng-container role="title">{{ title }}</ng-container>
      {{ content }}</es-alert
    >
    <br />
    <es-alert variant="error" [closable]="closable" (closed)="onClose()">
      <ng-container role="title">{{ title }}</ng-container>
      {{ content }}</es-alert
    >
  `,
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class AlertStoryIconMappingComponent {
  @Input() public content: string;
  @Input() public title: string;
  @Input() public closable: boolean;

  @Output() public closed = new EventEmitter();

  constructor(private matIconRegistry: MatIconRegistry, private domSanitizer: DomSanitizer) {
    this.matIconRegistry.addSvgIcon(
      'warning',
      this.domSanitizer.bypassSecurityTrustResourceUrl('/icons/alert/warning.svg')
    );

    this.matIconRegistry.addSvgIcon(
      'error',
      this.domSanitizer.bypassSecurityTrustResourceUrl('/icons/alert/error.svg')
    );
  }

  public onClose() {
    this.closed.emit();
  }
}

import { Component, Input, ViewEncapsulation } from '@angular/core';
import { UntypedFormBuilder, UntypedFormGroup } from '@angular/forms';

import { of } from 'rxjs';
import { map, tap } from 'rxjs/operators';

import { AutocompleteMultipleStoryService } from './autocomplete-multiple-story-service.service';

@Component({
  selector: 'es-autocomplete-multiple-story-service',
  styleUrls: ['./autocomplete-multiple-story-service.component.scss'],
  encapsulation: ViewEncapsulation.None,
  template: `
    <form class="es-autocomplete-multiple-story-service" [formGroup]="form">
      <mat-form-field
        appearance="outline"
        class="es-autocomplete-multiple-story-service__field"
        [ngStyle]="{ 'width.px': width }"
      >
        <mat-label>Country</mat-label>

        <es-autocomplete-multiple
          formControlName="autocomplete"
          [service]="searchService"
          [required]="required"
          [disabled]="disabled"
          [optionsCount]="totalOptionCount"
          [displayWith]="displayWith"
        ></es-autocomplete-multiple>
      </mat-form-field>
    </form>
  `
})
export class AutocompleteMultipleStoryServiceComponent {
  @Input() public width: number;

  @Input() public showedOptionCount = null;

  @Input() public required: boolean;

  @Input() public disabled: boolean;

  public form: UntypedFormGroup;

  public totalOptionCount: number;

  constructor(
    private service: AutocompleteMultipleStoryService,
    private formBuilder: UntypedFormBuilder
  ) {
    this.form = this.formBuilder.group({
      autocomplete: [
        [
          { id: 3, name: 'Norway' },
          { id: 9, name: 'Portugal' },
          { id: 10, name: 'Russia' }
        ]
      ]
    });
  }

  public searchService = (text: string, options?: any[]) => {
    const lowerText = text ? text.toLowerCase() : '';

    this.totalOptionCount = 0;

    if (options) {
      return of(options.filter((option) => option.name.toLowerCase().includes(lowerText))).pipe(
        tap((o) => {
          this.totalOptionCount = o.length;
        })
      );
    } else {
      return this.service.getOptions(lowerText, this.showedOptionCount).pipe(
        map((o) => {
          this.totalOptionCount = o.totalCount;

          return o.options;
        })
      );
    }
  };

  public displayWith = (value: { id: number; name: string }) => value.name;
}

