.es-paginator {
  align-items: baseline;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-between;
  white-space: nowrap;

  &__size {
    align-items: center;
    display: flex;
    margin-bottom: 16px;
    margin-right: 24px;
  }

  &__select {
    display: block;
    margin-left: 4px;
    margin-right: 18px;

    &.mat-mdc-select {
      width: 40px;
    }
